// @lc code=start
class Solution {
    public int[][] rangeAddQueries(int n, int[][] queries) {
        int[][] mat = new int[n][n];
        for(var query: queries) {
            int row1 =  query[0];
            int col1 = query[1];
            int row2 = query[2];
            int col2 = query[3];
            for(int i = row1; i <= row2; i++) {
                mat[i][col1] += 1;
                if (col2 < n - 1) mat[i][col2 + 1] -= 1;
            }
        }

        for(int i = 0; i < n; i++) {
            for(int j = 1; j < n; j++) {
                mat[i][j] += mat[i][j - 1];
            }
        }

        return mat;
    }
}
